import React, { useState ,useEffect } from "react";
import '../../folder_css/css_two.css'
import { useForm } from "react-hook-form";
import axios from "axios";

export default function ThemDanhMucSanPham() {
    const api ='http://localhost:3001/';
    const[list_category , setlist_category] = useState([]);
    const[tenDanhMucSanPham , setTenDanhMucSanPham] = useState('');
    const[idDanhMuc , setIDDanhMuc] = useState('');

    //code button thêm
    const submitEmployeeRecord = async (e) =>{ 
        const formData = new FormData(); 
        formData.append('ten', tenDanhMucSanPham);
        formData.append('danhmucsanpham', idDanhMuc);
        console.log(tenDanhMucSanPham );
        console.log(idDanhMuc );
          axios.post(api +'adddanhmucsanpham/',  formData )
          .then(res =>{
              if(res.data ==='ok'){
                alert("thêm thành công")
            }
            console.log(res);
          })     
      }

    //Hiển thị danh mục (getdanhmuc) bằng useEffect
    // useEffect(() => {
    //     getdanhmuc();
    //  }, []);
 //code hiển thị danh sách danh mục 
    //  const getdanhmuc = async() => {
    //    const base_url = api + 'danhmucsanpham/';
    //    const response = await axios.get(base_url)  // lấy dữ liệu ở trên base_url bằng hàm await 
    //    console.log(response.data); 
    //    setlist_category(response.data);  // hiển thị
    //  }
    return (
        <div className="form_add_cate">
            <h2 className="Text-Name">Thêm Danh Mục Sản Phẩm</h2> 
            <div className="form_nhap_thon_tin_danh_muc">
                <form>
                    <input className="Input-AddCategory" placeholder="Tên Danh Mục" onChange={(e) => setTenDanhMucSanPham(e.target.value)} />
                    <select className="Input-AddCategory" onChange={(e) => setIDDanhMuc(e.target.value)}>
                        <option value="0">Chọn Danh Mục</option>
                        {list_category.map((item)=>
                            <option key={item.madanhmucsanpham} value={item.madanhmucsanpham}>{item.tendanhmucsanpham}</option>
                        )}
                    </select>
                </form>
                <div className="Btn-Add">
                        <button className="button3" onClick={submitEmployeeRecord}>Thêm </button>
                        <button className="button4">Hủy </button>
                </div>
            </div>
       </div>
    );
  }
