import React ,{useState , useEffect} from "react";
import '../../folder_css/css_two.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from "react-router-dom";

export default function DanhMucSanPham() {
  const[list_category , setlist_category] = useState([]);

     //Hiển thị Danh Mục
     useEffect(() => {
      getdanhmuc();
  }, []);

//code hiển thị danh sách danh mục 
  const getdanhmuc = async() => {
     const base_url = api + 'danhmucsanpham/';
     const response = await axios.get(base_url)
     console.log(response.data); 
     setlist_category(response.data);
  }

    return (
      <div className="giao-dien-product">
        <h2 className="Text-Name">Danh Mục Sản Phẩm ADMIN</h2> 
        <div className="push-2-product">
          <Link to={`/Them-Danh-Muc-San_Pham`}>
            <button className="button1"><img alt="IMG_1" src={require("../../folder_css/img/10.png")} style={{marginRight: 10 ,width : 20 , height: 20 ,}}></img>Thêm Danh Mục</button>
          </Link>
          <button className="button2">Danh Sách Danh Mục</button>
          <input className="input-search"  placeholder="Tìm Kiếm..."></input><button>Tìm Kiếm</button>
          <hr  width="100%" size="5px" align="center" color="red"/>
        </div>
        <div className="List-product">
        <table className="table table-hover  table-striped table-bordered ml-4 ">
            <thead>
            <tr className="Text-Color">
                <th>ID Danh Mục</th>
                <th>Danh Mục Cha</th>
                <th>Danh Mục Con</th>
                <th>Hình Ảnh Danh Mục</th>
                <th>Chức năng</th>
            </tr>
            </thead>
            <tbody>
     
            {list_category.map((name)=>
                <tr key={name.masanpham}>
                <td>{name.masanpham}</td>
                <td>{name.tensanpham}</td>
                <td>{name.loaisanpham}</td>
                <td style={{textDecorationLine : 'line-through'}}>{name.giacu.toLocaleString()}</td>
                <td>{name.giasanpham.toLocaleString()}</td>
                <td>{name.hangsanxuat}</td>
                <td>
                <img className="card-img-top hover-shadow" src={""} alt="Chưa có hình ảnh trong file nên không hiển thị" style={{width:"120px", height:"100%"}}/>
                </td>
                {/* <td>

                        <button className="magin_btn" 
                          onClick={() => { 
                          const confirmBox = window.confirm(
                            "Bạn chắc chắn muốn xóa Tên Danh Mục : "+ name.tensanpham +""
                          )
                          if (confirmBox === true) {     
                            deleteRecord(name.masanpham)                   
                          }
                          
                        }}>   Xóa    </button>

                        <Link to={`/editsanpham/${name.masanpham}`}>
                        <button className="magin_btn">
                          Sửa   
                        </button>
                        </Link>

                </td> */}
                </tr>
                )} 
            </tbody>
        </table>
        </div>      
      </div>
    );
  }
